// QuotationPDF.js
import React from "react";
import { Document, Page, Text, View, StyleSheet } from "@react-pdf/renderer";

const styles = StyleSheet.create({
  page: {
    flexDirection: "column",
    padding: 10,
  },
  companyName: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 10,
  },
});

const QuotationPDF = ({ companyName, tableData }) => (
  <Document>
    <Page size="A4" style={styles.page}>
      <View>
        <Text style={styles.companyName}>{companyName}</Text>
        {/* Render your quotation details here */}
        <Text>Quotation Details:</Text>
        <View>
          <table>
            <thead>
              <tr>
                <th>Brand</th>
                <th>Model</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((item, index) => (
                <tr key={index}>
                  <td>{item.brand}</td>
                  <td>{item.model}</td>
                  <td>${item.price}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </View>
      </View>
    </Page>
  </Document>
);

export default QuotationPDF;
