import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from 'react-google-login';
import Logo from './2.png';
import './login.css';
import axios from 'axios';

export default function Login() {
    const [Email, setEmail] = useState('');
    const [Password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const GOOGLE_CLIENTID ="435896067635-qnnts982dckt09ne136a37mh0efb0ej5.apps.googleusercontent.com"
    const navigate = useNavigate();

    const responseGoogle = async (response) => {
        const { tokenId } = response;
        try {
            setLoading(true);
            const res = await axios.post('http://localhost:3002/user/google-login', { tokenId });
            const { token, role } = res.data;
            if (token) {
                localStorage.setItem('token', token);
                if (role === 'admin') {
                    alert('Admin login successful');
                    navigate('/admin');
                } else {
                    alert('User login successful');
                    navigate('/');
                }
            } else {
                alert('Login failed. Please check your credentials.');
            }
        } catch (error) {
            console.error('Error logging in with Google:', error);
            alert('An error occurred while logging in with Google. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleEmail = (e) => {
        setEmail(e.target.value);
    };

    const handlePassword = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Your existing login logic here
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <div className="login-image">
                    <img src={Logo} alt="Logo" />
                </div>
                <div className="login-center">
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        {/* Your existing form */}
                    </form>
                    <div className="google__login">
                        <GoogleLogin
                            clientId="YOUR_CLIENT_ID"
                            buttonText="Sign in with Google"
                            onSuccess={responseGoogle}
                            onFailure={responseGoogle}
                            cookiePolicy={'single_host_origin'}
                        />
                    </div>
                    {/* Other elements */}
                </div>
            </div>
        </div>
    );
}
