import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Logo from './2.png';
import './login.css';
import { LoginSocialGoogle } from 'reactjs-social-login';
import axios from 'axios';

export default function Login() {
    const [Email, setEmail] = useState('');
    const [Password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!Email || !Password) {
            toast.error('Please enter both email and password.');
            return;
        }

        setLoading(true);

        try {
            const response = await axios.post('http://localhost:3002/user/login', { Email, Password });
            const { token, role } = response.data;

            if (token) {
                localStorage.setItem('token', token);
                if (role === 'admin') {
                    toast.success('Admin login successful');
                    navigate('/admin');
                } else {
                    toast.success('User login successful');
                    navigate('/');
                }
            } else {
                toast.error('Login failed. Please check your credentials.');
            }
        } catch (error) {
            console.error('Error logging in:', error);
            toast.error('An error occurred while logging in. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleEmail = (e) => {
        setEmail(e.target.value);
    };

    const handlePassword = (e) => {
        setPassword(e.target.value);
    };

    return (
        <div className="login-container">
            <ToastContainer />
            <div className="login-form">
                <div className="login-image">
                    <img src={Logo} alt="Logo" />
                </div>
                <div className="login-center">
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        <div className="login-input">
                            <div className="login-txt_field">
                                <input type="email" value={Email} onChange={handleEmail} required />
                                <span className="spanclass"></span>
                                <label htmlFor="email">Email Address</label>
                            </div>
                            <div className="login-txt_field">
                                <input type="password" value={Password} onChange={handlePassword} required />
                                <span className="spanclass"></span>
                                <label htmlFor="password">Password</label>
                            </div>
                        </div>
                        <div className="login-btn">
                            <button type="submit" className="login-btn-no1" disabled={loading}>
                                {loading ? 'Loading...' : 'Login'}
                            </button>
                        </div>
                    </form>
                    <div className="forgot">Forgot Password?</div>
                    <div className="signup_link">
                        Not a Member? <a href="/signup">Create New Account</a>
                    </div>
                </div>
            </div>
        </div>
    );
}
