import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from 'react-google-login';
import Logo from './2.png';
import './login.css';
import axios from 'axios';
import { GoogleOAuthProvider } from '@react-oauth/google';

export default function Login() {
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const responseGoogle = async (response) => {
        try {
            setLoading(true);
            // Send the token ID to your backend for verification
            const res = await axios.post('http://localhost:3002/user/google-login', { tokenId: response.tokenId });
            const { token, role } = res.data;
            if (token) {
                localStorage.setItem('token', token);
                if (role === 'admin') {
                    alert('Admin login successful');
                    navigate('/admin');
                } else {
                    alert('User login successful');
                    navigate('/');
                }
            } else {
                alert('Login failed. Please check your credentials.');
            }
        } catch (error) {
            console.error('Error logging in with Google:', error);
            alert('An error occurred while logging in with Google. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-form">
                <div className="login-image">
                    <img src={Logo} alt="Logo" />
                </div>
                <div className="login-center">
                    <h1>Login</h1>
                    <GoogleLogin
                        clientId="YOUR_CLIENT_ID"
                        buttonText="Sign in with Google"
                        onSuccess={responseGoogle}
                        onFailure={responseGoogle}
                        cookiePolicy={'single_host_origin'}
                        disabled={loading}
                    />
                    <div className="divider">
                        <span>or</span>
                    </div>
                        <div className="login-input">
                            <div className="login-txt_field">
                                <input type="email" value={Email} onChange={handleEmail} required />
                                <span className="spanclass"></span>
                                <label htmlFor="email">Email Address</label>
                            </div>
                            <div className="login-txt_field">
                                <input type="password" value={Password} onChange={handlePassword} required />
                                <span className="spanclass"></span>
                                <label htmlFor="password">Password</label>
                            </div>
                        </div>
                        <div className="login-btn">
                            <button type="submit" className="login-btn-no1" disabled={loading}>
                                {loading ? 'Loading...' : 'Login'}
                            </button>
                        </div>
                    <div className="forgot">Forgot Password?</div>
                    <div className="signup_link">
                        Not a Member? <a href="/signup">Create New Account</a>
                    </div>

                </div>
            </div>
        </div>
    );
}
